import React from 'react';

const MostraVoltas = (props) => {
  return (
    <p>
    {props.voltas} <br/>
    Voltas
    </p>
  )
}

const MostraTempo = (props) => {
  return (
    <p>
      {props.tempo}<br/>
      Tempo m√©dio por volta
    </p>
  )
}

const Button = (props) => <button>{props.text}</button> 

function App() {

  let numVoltas = 14

  const increment = () => {
    numVoltas++
    console.log('increment');

  }

  const decrement = () =>{

      numVoltas--
    console.log('');

  }



  return (
    <div>
      <MostraVoltas voltas='15'/>
      <Button text='+' onClick='increment'/> 
      <Button text='-' onClick='decrement'/><br /> 
      <MostraTempo tempo='01:36' />
      <Button text='Iniciar' />
      <Button text='Reiniciar' /><br/>

    </div>
  )
}

export default App;
