import React, { useState, useEffect } from 'react';
import './style.css' 

import MostraVoltas from './MostraVoltas'
import MostraTempo from './MostraTempo'
import Button from './Button'




function App() {
  const [numVoltas, setNumVoltas] = useState(0)
  const [running, setRunning] = useState(false)
  const [tempo, setTempo] = useState(0)

  console.log('toggle')

  useEffect(() => {
    let timer = null
    if (running) {
      setInterval(() => {
        setTempo(old => old + 1)
      }, 1000)
    }
    return () => { 
        if(timer){ // valor que é convertido para verdadeiro
          clearInterval(timer)
        }
    }
  }, [running])


  const toggleRunning = () => {
    setRunning(!running)
  }

  const increment = () => {
    setNumVoltas(numVoltas + 1)
  }

  const decrement = () =>{
    if (numVoltas > 0) {
      setNumVoltas(numVoltas - 1)
    }

  }

  const reset = () => {
    setNumVoltas(0)
    setTempo(0)
  }



  return (
    <div>
      <MostraVoltas voltas={numVoltas} />
      <Button text='+'  className='bigger' onClick={increment}/> 
      <Button text='-' className='bigger' onClick={decrement}/><br /> 
      {
        numVoltas > 0 &&
        <MostraTempo tempo={tempo/numVoltas} />
      }
      <Button onClick={toggleRunning} text={running ? 'Pausar' : 'Iniciar'} />
      <Button onClick={reset} text='Reiniciar' /><br/>

    </div>
  )
}

export default App;
