import React, { useState, useEffect } from 'react';

const MostraVoltas = (props) => {
  return (
    <p>
    {props.voltas} <br/>
    Voltas
    </p>
  )
}

const MostraTempo = (props) => {

  const tempo = props.tempo
  const minutos = Math.round(tempo / 60)
  const segundos = tempo % 60
  const minutosStr = minutos < 10 ? '0' + minutos : minutos
  const segundosStr = segundos < 10 ? '0' + segundos : segundos

  return (
    <p>
      {`${minutosStr}:${segundosStr}`}<br/>
      Tempo médio por volta
    </p>
  )
}

const Button = (props) => <button onClick={props.onClick}>{props.text}</button> 

function App() {

  const [numVoltas, setNumVoltas] = useState(0)
  const [running, setRunning] = useState(false)
  const [tempo, setTempo] = useState(0)

  console.log('toggle')

  useEffect(() => {
    let timer = null
    if (running) {
      setInterval(() => {
        setTempo(old => old + 1)
      }, 1000)
    }
    return () => { 
        if(timer){ // valor que é convertido para verdadeiro
          clearInterval(timer)
        }
    }
  }, [running])


  const toggleRunning = () => {
    setRunning(!running)
  }

  const increment = () => {
    setNumVoltas(numVoltas + 1)
  }

  const decrement = () =>{
    setNumVoltas(numVoltas - 1)
  }

  const reset = () => {
    setNumVoltas(0)

  }



  return (
    <div>
      <MostraVoltas voltas={numVoltas}/>
      <Button text='+' onClick={increment}/> 
      <Button text='-' onClick={decrement}/><br /> 
      {
        numVoltas > 0 &&
        <MostraTempo tempo={tempo/numVoltas} />
      }
      <Button  onClick={toggleRunning} text='Iniciar' />
      <Button onClick={reset} text='Reiniciar' /><br/>

    </div>
  )
}

export default App;
