import React from 'react';

const MostraVoltas = (props) => {
  return (
    <p>
    {props.voltas} <br/>
    Voltas
    </p>
  )
}

const MostraTempo = (props) => {
  return (
    <p>
      {props.tempo}<br/>
      Tempo m√©dio por volta
    </p>
  )
}

const Button = (props) => <button>{props.text}</button> 

function App() {
  return (
    <div className="App">
      <MostraVoltas voltas='15'/>
      <Button text='+' /> 
      <Button text='-' /><br /> 
      <MostraTempo tempo='01:36' />
      <Button text='Iniciar' /><br/> 

      <button>Reiniciar</button><br/>
    </div>
  )
}

export default App;
